import UIKit
import PromiseKit

class ImagesCollectionWorker
{
    func getPhotos(rover: String, earthDate: String) -> Promise<[Photo]>{
        return Webservice.GetPhotos.getMarsPhotos(rover: rover, earthDate: earthDate)
  }
}
